// frontend/src/App.tsx

import React, { useState, useEffect } from 'react';
import { Sidebar } from './components/Layout/Sidebar';
import { DocumentTable } from './components/Search/DocumentTable';
import { RenovationROI } from './components/Automate/RenovationROI';
import { GCMatch } from './components/Automate/GCMatch';
import { FactSheet } from './components/Snapshot/FactSheet';
import { ChatInterface } from './components/Copilot/ChatInterface';
import { FileUpload } from './components/Common/FileUpload';
import { LapisUsage } from '../../shared/types';
import { Bot, X } from 'lucide-react';

// Mock API service
const api = {
  uploadDocuments: async (files: File[]) => {
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 2000));
    return files.map((file, index) => ({
      id: `doc-${Date.now()}-${index}`,
      filename: file.name,
      type: file.name.toLowerCase().includes('mls') ? 'mls' : 'deed',
      uploadedAt: new Date(),
      status: 'completed',
      address: '123 Main St',
      beds: 3,
      baths: 2,
      squareFootage: 1500,
      listingPrice: 350000,
      estimatedARV: 450000,
      yearBuilt: 2005,
      infrastructureScore: 85,
      lienStatus: 'Clear'
    }));
  },
  
  sendChatMessage: async (conversationId: string, message: string) => {
    await new Promise(resolve => setTimeout(resolve, 1000));
    return {
      message: {
        content: "I've analyzed your property documents. The property at 123 Main St shows strong investment potential with an estimated ARV of $450,000. Would you like me to run a detailed Renovation ROI analysis?",
        intent: 'automate'
      },
      suggestedActions: ['Run Renovation ROI', 'View Property Details', 'Generate Fact Sheet']
    };
  }
};

function App() {
  const [activeTask, setActiveTask] = useState<'search' | 'automate' | 'snapshot'>('search');
  const [activeAutomateTab, setActiveAutomateTab] = useState<'roi' | 'gc'>('roi');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [isChatExpanded, setIsChatExpanded] = useState(false);
  const [documents, setDocuments] = useState<any[]>([]);
  const [selectedDocuments, setSelectedDocuments] = useState<any[]>([]);
  const [rehabScope, setRehabScope] = useState<any>(null);
  const [lapisUsage, setLapisUsage] = useState<LapisUsage>({
    search: 0,
    automate: 0,
    snapshot: 0,
    remaining: 210,
    total: 210
  });

  const conversationId = 'conv-' + Date.now();

  const handleUpload = async (files: File[]) => {
    const newDocs = await api.uploadDocuments(files);
    setDocuments(prev => [...prev, ...newDocs]);
    
    // Update Lapis usage
    setLapisUsage(prev => ({
      ...prev,
      search: prev.search + (files.length * 2),
      remaining: prev.remaining - (files.length * 2)
    }));
  };

  const handleDocumentSelect = (docIds: string[]) => {
    const selected = documents.filter(doc => docIds.includes(doc.id));
    setSelectedDocuments(selected);
  };

  const handleGenerateReport = (scope: any) => {
    setRehabScope(scope);
    setLapisUsage(prev => ({
      ...prev,
      automate: prev.automate + 8,
      remaining: prev.remaining - 8
    }));
  };

  const handleSendChat = async (message: string) => {
    const response = await api.sendChatMessage(conversationId, message);
    return response;
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar
        activeTask={activeTask}
        onTaskChange={setActiveTask}
        lapisUsage={lapisUsage}
        isCollapsed={isSidebarCollapsed}
        onToggleCollapse={() => setIsSidebarCollapsed(!isSidebarCollapsed)}
      />

      {/* Main Content */}
      <div className="flex-1 flex overflow-hidden">
        <div className="flex-1 overflow-auto">
          {/* Header */}
          <div className="bg-white shadow-sm px-6 py-4 flex items-center justify-between">
            <h1 className="text-2xl font-bold">
              {activeTask === 'search' && 'Search & Explore Documents'}
              {activeTask === 'automate' && 'Automate Analysis'}
              {activeTask === 'snapshot' && 'Generate Snapshots'}
            </h1>
            
            <div className="flex items-center gap-2">
              <span className="px-3 py-1 bg-blue-100 text-blue-700 rounded-full text-sm font-medium">
                TRIAL â€¢ {lapisUsage.remaining} Lapis
              </span>
            </div>
          </div>

          {/* Content Area */}
          <div className="p-6">
            {activeTask === 'search' && (
              <>
                {documents.length === 0 ? (
                  <div className="max-w-2xl mx-auto">
                    <FileUpload onUpload={handleUpload} />
                  </div>
                ) : (
                  <DocumentTable
                    documents={documents}
                    onFieldUpdate={() => {}}
                    onDocumentSelect={handleDocumentSelect}
                  />
                )}
              </>
            )}

            {activeTask === 'automate' && (
              <>
                {/* Automate Tabs */}
                <div className="flex gap-4 mb-6">
                  <button
                    onClick={() => setActiveAutomateTab('roi')}
                    className={`px-4 py-2 rounded-lg font-medium ${
                      activeAutomateTab === 'roi'
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                    }`}
                  >
                    Renovation ROI
                  </button>
                  <button
                    onClick={() => setActiveAutomateTab('gc')}
                    className={`px-4 py-2 rounded-lg font-medium ${
                      activeAutomateTab === 'gc'
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                    }`}
                  >
                    GC-Match
                  </button>
                </div>

                {activeAutomateTab === 'roi' ? (
                  <RenovationROI
                    selectedDocuments={selectedDocuments}
                    onGenerateReport={handleGenerateReport}
                  />
                ) : (
                  <GCMatch
                    rehabScope={rehabScope}
                    onSendRFPs={() => {}}
                  />
                )}
              </>
            )}

            {activeTask === 'snapshot' && (
              <FactSheet
                selectedDocuments={selectedDocuments}
                rehabScope={rehabScope}
                onGenerateFactSheet={() => {
                  setLapisUsage(prev => ({
                    ...prev,
                    snapshot: prev.snapshot + 1,
                    remaining: prev.remaining - 1
                  }));
                }}
              />
            )}
          </div>
        </div>

        {/* Copilot Chat Sidebar */}
        <div className={`transition-all duration-300 ${
          isChatExpanded ? 'w-96' : 'w-0'
        } bg-white shadow-lg overflow-hidden flex flex-col`}>
          <ChatInterface
            conversationId={conversationId}
            onSendMessage={handleSendChat}
            isExpanded={isChatExpanded}
          />
        </div>
      </div>

      {/* Floating Copilot Button */}
      <button
        onClick={() => setIsChatExpanded(!isChatExpanded)}
        className={`fixed bottom-6 right-6 w-14 h-14 rounded-full shadow-lg flex items-center justify-center transition-all ${
          isChatExpanded 
            ? 'bg-gray-600 hover:bg-gray-700' 
            : 'bg-blue-600 hover:bg-blue-700 animate-pulse'
        }`}
      >
        {isChatExpanded ? (
          <X className="text-white" size={24} />
        ) : (
          <Bot className="text-white" size={24} />
        )}
      </button>
    </div>
  );
}

export default App;
